    "content": "import 'package:flutter/material.dart';

class ExploreScreen extends StatefulWidget {
  const ExploreScreen({super.key});

  @override
  State<ExploreScreen> createState() => _ExploreScreenState();
}

class _ExploreScreenState extends State<ExploreScreen> {
  String? _selectedCategory;
  final List<String> _categories = ['Popular', 'New', 'Design', 'Development', 'Marketing', 'Business'];

  @override
  Widget build(BuildContext context) {
    final textTheme = Theme.of(context).textTheme;
    final colorScheme = Theme.of(context).colorScheme;

    return Scaffold(
      appBar: AppBar(
        // M3_COMPLIANCE_WIDGET_ADOPTION: Use the Material 3 SearchBar Widget
        // For a simple AppBar search, SearchBar can be placed directly or wrapped.
        // For full-screen search behavior, it's typically used with a SearchAnchor.
        // Here, we'll implement a simple SearchBar in the AppBar.
        title: SearchBar(
          elevation: MaterialStateProperty.all(0),
          backgroundColor: MaterialStateProperty.all(colorScheme.surfaceContainerHighest),
          hintText: 'Search for courses or teachers...',
          leading: Icon(Icons.search, color: colorScheme.onSurfaceVariant),
          textStyle: MaterialStateProperty.all(textTheme.bodyLarge),
          onTap: () {
            // Implement navigation to a dedicated search screen or full-screen search anchor
          },
          onChanged: (value) {
            // Handle search input changes
          },
        ),
        toolbarHeight: 80, // Give some space for the SearchBar
      ),
      body: SingleChildScrollView(
        padding: const EdgeInsets.symmetric(vertical: 16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Padding(
              padding: const EdgeInsets.symmetric(horizontal: 16.0),
              child: Text(
                'Discover New Content',
                style: textTheme.headlineSmall?.copyWith(fontWeight: FontWeight.bold),
              ),
            ),
            const SizedBox(height: 16),

            // Category Filters: Chip Group
            // LAYOUT_INTRINSIC_SIZING: Remove Fixed Height on Chip List
            // ACCESSIBILITY_TOUCH_TARGET_SIZE: Allow chips to determine their own height for better touch targets.
            SizedBox(
              // No fixed height, let the content define it
              child: Padding(
                padding: const EdgeInsets.symmetric(horizontal: 16.0),
                child: Wrap(
                  spacing: 8.0, // Horizontal spacing between chips
                  runSpacing: 8.0, // Vertical spacing between rows of chips
                  children: _categories.map((category) {
                    return FilterChip(
                      label: Text(category),
                      selected: _selectedCategory == category,
                      onSelected: (bool selected) {
                        setState(() {
                          _selectedCategory = selected ? category : null;
                        });
                      },
                      // Unselected chips are Neutral Light (surfaceContainerHigh), selected chips use Primary color
                      selectedColor: colorScheme.primary,
                      backgroundColor: colorScheme.surfaceContainerHigh,
                      labelStyle: _selectedCategory == category
                          ? textTheme.labelLarge?.copyWith(color: colorScheme.onPrimary)
                          : textTheme.labelLarge?.copyWith(color: colorScheme.onSurface),
                      showCheckmark: false,
                      padding: const EdgeInsets.symmetric(horizontal: 12, vertical: 8), // Ensure good padding
                    );
                  }).toList(),
                ),
              ),
            ),
            const SizedBox(height: 24),

            // Content Grid: Card Grid
            Padding(
              padding: const EdgeInsets.symmetric(horizontal: 16.0),
              child: GridView.builder(
                shrinkWrap: true,
                physics: const NeverScrollableScrollPhysics(),
                gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
                  crossAxisCount: 2,
                  crossAxisSpacing: 16.0,
                  mainAxisSpacing: 16.0,
                  childAspectRatio: 0.8, // Adjust as needed
                ),
                itemCount: 8, // Example count
                itemBuilder: (context, index) {
                  return _buildCourseCard(context, index);
                },
              ),
            ),
          ],
        ),
      ),
    );
  }

  Widget _buildCourseCard(BuildContext context, int index) {
    final textTheme = Theme.of(context).textTheme;
    final colorScheme = Theme.of(context).colorScheme;

    return Card(
      clipBehavior: Clip.antiAlias,
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
