class MyLearningScreen extends StatefulWidget {
  const MyLearningScreen({super.key});

  @override
  State<MyLearningScreen> createState() => _MyLearningScreenState();
}

class _MyLearningScreenState extends State<MyLearningScreen> with SingleTickerProviderStateMixin {
  late TabController _tabController;

  @override
  void initState() {
    super.initState();
    _tabController = TabController(length: 2, vsync: this);
  }

  @override
  void dispose() {
    _tabController.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    final textTheme = Theme.of(context).textTheme;
    final colorScheme = Theme.of(context).colorScheme;

    return Scaffold(
      appBar: AppBar(
        // HIERARCHY_TYPOGRAPHY_INCONSISTENCY: Consistent AppBar Title Style
        // Removed explicit textTheme.headlineSmall to use global appBarTheme.titleTextStyle
        title: const Text('My Learning'),
        bottom: TabBar(
          controller: _tabController,
          tabs: const [
            Tab(text: 'In Progress'),
            Tab(text: 'Completed'),
          ],
        ),
      ),
      body: TabBarView(
        controller: _tabController,
        children: [
          // In Progress Courses
          _buildCourseListView(context, isInProgress: true),
          // Completed Courses
          _buildCourseListView(context, isInProgress: false),
        ],
      ),
    );
  }

  Widget _buildCourseListView(BuildContext context, {required bool isInProgress}) {
    final textTheme = Theme.of(context).textTheme;
    final colorScheme = Theme.of(context).colorScheme;

    final itemCount = isInProgress ? 4 : 2; // Example counts

    if (itemCount == 0) {
      return Center(
        child: Text(
          isInProgress ? 'No courses in progress.' : 'No completed courses yet.',
          style: textTheme.titleMedium?.copyWith(color: colorScheme.onSurfaceVariant),
        ),
      );
    }

    return ListView.builder(
      padding: const EdgeInsets.all(16.0),
      itemCount: itemCount,
      itemBuilder: (context, index) {
        return Padding(
          padding: const EdgeInsets.only(bottom: 16.0),
          child: Card(
            child: Padding(
              padding: const EdgeInsets.all(16.0),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  Row(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    children: [
                      Container(
                        width: 80,
                        height: 80,
                        decoration: BoxDecoration(
                          color: colorScheme.surfaceContainerHighest,
                          borderRadius: BorderRadius.circular(8),
                          image: DecorationImage(
                            image: NetworkImage('https://picsum.photos/seed/${400 + index}/200/200'),
                            fit: BoxFit.cover,
                          ),
                        ),
                      ),
                      const SizedBox(width: 16),
                      Expanded(
                        child: Column(
                          crossAxisAlignment: CrossAxisAlignment.start,
                          children: [
                            Text(
                              'Course Title ${index + 1}',
                              style: textTheme.titleMedium?.copyWith(fontWeight: FontWeight.bold),
                              maxLines: 2,
                              overflow: TextOverflow.ellipsis,
                            ),
                            const SizedBox(height: 4),
                            Text(
                              'by Teacher Name',
                              style: textTheme.bodyMedium?.copyWith(color: colorScheme.onSurfaceVariant),
                              maxLines: 1,
                              overflow: TextOverflow.ellipsis,
                            ),
                            const SizedBox(height: 8),
                            if (isInProgress)
                              LinearProgressIndicator(
                                value: (index + 1) * 0.2, // Example progress
                                backgroundColor: colorScheme.surfaceContainerHighest,
                                valueColor: AlwaysStoppedAnimation<Color>(colorScheme.primary), // Accent color
                                minHeight: 8,
                                borderRadius: BorderRadius.circular(10),
                              )
                            else
                              // ACCESSIBILITY_COLOR_CONTRAST_SEMANTIC: Hardcoded 'Completed' status color is inaccessible.
                              // SUGGESTION: THEME_SEMANTIC_COLORS: Define Semantic Colors in the Theme.
                              // Using colorScheme.tertiary for a distinct, accessible 'completed' status color.
                              Text(
                                'Completed',
                                style: textTheme.bodyLarge?.copyWith(
                                  color: colorScheme.tertiary, // Use tertiary for 'completed' status
                                  fontWeight: FontWeight.bold,
                                ),
                              ),
                          ],
                        ),
                      ),
                    ],
                  ),
                  if (isInProgress) ...[
                    const SizedBox(height: 12),
                    Align(
                      alignment: Alignment.centerRight,
                      child: Text(
                        '${((index + 1) * 20).toInt()}% Complete',
                        style: textTheme.labelMedium?.copyWith(color: colorScheme.onSurfaceVariant),
                      ),
                    ),
                  ],
                ],
              ),
            ),
          ),
        );
      },
    );
  }
}
