  Widget _buildFilterChip(BuildContext context, String label, int index) {
    final theme = Theme.of(context);
    return ChoiceChip(
      label: Text(label),
      selected: _selectedChipIndex == index,
      selectedColor: theme.primaryColor, // Uses Primary color for selected chips
      backgroundColor: Colors.grey[200], // Neutral Light for unselected chips
