class _MyLearningScreenState extends State<MyLearningScreen> with SingleTickerProviderStateMixin {
  late TabController _tabController;

  @override
  void initState() {
    super.initState();
    _tabController = TabController(length: 2, vsync: this);
  }

  @override
  void dispose() {
    _tabController.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    final theme = Theme.of(context);
    return Scaffold(
      appBar: AppBar(
        title: const Text('My Learning'),
        bottom: TabBar(
          controller: _tabController,
          labelColor: theme.primaryColor,
          unselectedLabelColor: Colors.grey,
          indicatorColor: theme.primaryColor,
          labelStyle: theme.textTheme.titleMedium?.copyWith(fontWeight: FontWeight.bold),
          unselectedLabelStyle: theme.textTheme.titleMedium,
          tabs: const [
            Tab(text: 'In Progress'),
            Tab(text: 'Completed'),
          ],
        ),
      ),
      body: TabBarView(
        controller: _tabController,
        children: [
          // In Progress Courses
          _buildCourseList(context, isInProgress: true),
          // Completed Courses
          _buildCourseList(context, isInProgress: false),
        ],
      ),
    );
  }

  Widget _buildCourseList(BuildContext context, {required bool isInProgress}) {
    final theme = Theme.of(context);
    List<Map<String, dynamic>> courses = isInProgress
        ? [
            {'title': 'Advanced Flutter State Management', 'teacher': 'Alice Johnson', 'progress': 0.6},
            {'title': 'UI/UX Design Principles', 'teacher': 'Bob Williams', 'progress': 0.3},
            {'title': 'Introduction to Dart Programming', 'teacher': 'Charlie Brown', 'progress': 0.9},
          ]
        : [
            {'title': 'Flutter Basics for Beginners', 'teacher': 'Diana Prince', 'progress': 1.0},
            {'title': 'Mobile App Marketing Strategies', 'teacher': 'Eve Adams', 'progress': 1.0},
          ];

    if (courses.isEmpty) {
      return Center(
        child: Text(
          isInProgress ? 'No courses in progress.' : 'No completed courses yet.',
          style: theme.textTheme.titleMedium?.copyWith(color: Colors.grey[600]),
        ),
      );
    }

    return ListView.builder(
      padding: const EdgeInsets.all(16.0),
      itemCount: courses.length,
      itemBuilder: (context, index) {
        final course = courses[index];
        return Card(
          margin: const EdgeInsets.only(bottom: 16.0),
          child: Padding(
            padding: const EdgeInsets.all(16.0),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  course['title'],
                  style: theme.textTheme.titleLarge?.copyWith(fontWeight: FontWeight.bold),
                  maxLines: 2,
                  overflow: TextOverflow.ellipsis,
                ),
                const SizedBox(height: 8),
                Text(
                  'By ${course['teacher']}',
                  style: theme.textTheme.titleSmall?.copyWith(color: Colors.grey[600]),
                ),
                const SizedBox(height: 16),
                if (isInProgress) ...[
                  LinearProgressIndicator(
                    value: course['progress'],
                    backgroundColor: Colors.grey[300],
                    valueColor: AlwaysStoppedAnimation<Color>(theme.colorScheme.secondary), // Accent color
                    minHeight: 8,
                    borderRadius: BorderRadius.circular(4),
                  ),
                  const SizedBox(height: 8),
                  Align(
                    alignment: Alignment.centerRight,
                    child: Text(
                      '${(course['progress'] * 100).toInt()}% Complete',
                      style: theme.textTheme.bodySmall?.copyWith(color: Colors.grey[600]),
                    ),
                  ),
                ] else ...[
                  Row(
                    children: [
                      Icon(Icons.check_circle, color: Colors.green, size: 20),
                      const SizedBox(width: 8),
                      Text(
                        'Completed!',
                        style: theme.textTheme.bodyLarge?.copyWith(color: Colors.green, fontWeight: FontWeight.bold),
                      ),
                    ],
                  ),
                ],
                const SizedBox(height: 16),
                Align(
                  alignment: Alignment.centerRight,
                  child: ElevatedButton(
                    onPressed: () {
                      // Navigate to course details or lesson
                    },
                    child: Text(isInProgress ? 'Continue' : 'View Certificate'),
                  ),
                ),
              ],
            ),
          ),
        );
      },
    );
  }
}
